mapper.update:
UPDATE contract
SET contract_status=#{status}, contract_start_date=now(), contract_end_date=date_add(now(),INTERVAL contract_date MONTH), mod_date=now()
WHERE contract_id=#{id}

update.foreach:
UPDATE contract
SET contract_status=#{status}, contract_start_date=now(), contract_end_date=date_add(now(),INTERVAL contract_date MONTH), mod_date=now()
WHERE contract_id=#{id}
