mapper.select:
SELECT p.product_id, p.product_name, p.product_brand, p.storage_type, p.product_cost, p.product_selling, p.product_info, p.inbound_box_width, p.inbound_box_length, p.inbound_box_height, p.product_weight, p.product_img, c.subclass_id, c.subclass_name, m.middle_name, j.major_name FROM product p, product_subclass_category c, product_middle_category m, product_major_category j WHERE p.subclass_id = c.subclass_id AND c.middle_id = m.middle_id AND m.major_id = j.major_id
AND p.product_name LIKE CONCAT('%', #{searchCond.productName}, '%')
AND p.product_brand LIKE CONCAT('%', #{searchCond.productBrand}, '%')
AND j.major_id = #{searchCond.majorCategoryId}
AND m.middle_id = #{searchCond.middleCategoryId}
AND c.subclass_id = #{searchCond.subclassCategoryId}
LIMIT #{skip}, #{size}

SELECT COUNT(*) FROM product p LEFT JOIN product_subclass_category c ON p.subclass_id = c.subclass_id LEFT JOIN product_middle_category m ON c.middle_id = m.middle_id LEFT JOIN product_major_category j ON m.major_id = j.major_id WHERE 1=1
AND p.product_name LIKE CONCAT('%', #{productName}, '%')
AND p.product_brand LIKE CONCAT('%', #{productBrand}, '%')
AND j.major_id = #{majorCategoryId}
AND m.middle_id = #{middleCategoryId}
AND c.subclass_id = #{subclassCategoryId}
